INSERT INTO `wp_posts` VALUES("195", "1", "2014-08-05 13:05:31", "2014-08-05 13:05:31", "Welcome to IIT Hyderabad Research Portal\r\nThe PhD programme at the Indian Institute of Technology Hyderabad encourages quality and original research. Currently, IITH offers research degrees in four broad areas, namely, Engineering, Sciences, Liberal Arts and Design. Equipped with international standards research facilities, the faculty here aims to arm the researchers with excellent academic abilities to orient them towards a career in the contemporary world of research. It is, therefore, through the rendition of focused guidance in the development of an individual’s academic as well as creative and humanitarian aspect that IITH seeks to make its mark.\r\n", "Welcome to IIT Hyderabad Research Portal", "", "inherit", "open", "open", "", "32-revision-v1", "", "", "2014-08-05 13:05:31", "2014-08-05 13:05:31", "", "32", "http://localhost:81/wordpress/2014/08/05/32-revision-v1/", "0", "revision", "", "0");
